import React, { Component } from "react";
import Swiper from "react-id-swiper";
import { Container } from "react-bootstrap";
import data from './images'

class Gallery extends Component {
    constructor(){
        super()
        this.state = {
            data: [],
        }
    }

    componentDidMount(){
        this.setState({ data: data})
    }
    render() {
        const { data } = this.state;
        console.log(data)
        const params = {
            effect: 'coverflow',
            grabCursor: true,
            centeredSlides: true,
            slidesPerView: 'auto',
            coverflowEffect: {
              rotate: 50,
              stretch: 0,
              depth: 100,
              modifier: 1,
              slideShadows: true
            },
            pagination: {
              el: '.swiper-pagination'
            }
          }
      
        return (
            <div>
                <Container className="mt-4 mb-5 pb-5 d-flex flex-row">
                    <Swiper {...params}>
                        <div>
                            <img
                                src="https://i.pinimg.com/564x/6a/d1/73/6ad1734962b2fa0a6f5fbf49f32bb26b.jpg"
                                className="img-fluid"
                                alt="slide"
                            />
                        </div>
                    </Swiper>
                </Container>
            </div>
        );
    }
}

export default Gallery;
