import React, { Component } from "react";
import Swiper from "react-id-swiper";
import { Container } from "react-bootstrap";
import data from "./images";

class Gallery extends Component {
    constructor() {
        super();
        this.state = {
            data: []
        };
    }

    componentDidMount() {
        this.setState({ data: data });
    }
    render() {
        const { data } = this.state;
        console.log(data);
        const params = {
            effect: "coverflow",
            grabCursor: true,
            centeredSlides: true,
            slidesPerView: "auto",
            coverflowEffect: {
                rotate: 50,
                stretch: 0,
                depth: 100,
                modifier: 1,
                slideShadows: true
            },
            pagination: {
                el: ".swiper-pagination"
            }
        };

        return (
            <div>
                <Container >
                    <Swiper {...params}>
                        {data.map((data, i) => {
                            return (
                                <div key={i}>
                                    <img
                                        src={data.image}
                                        key={data.id}
                                        className="img-slider img-fluid"
                                        alt="slider"
                                    />
                                </div>
                            );
                        })}
                    </Swiper>
                </Container>
            </div>
        );
    }
}

export default Gallery;
